mapper.select:
select review_id, comment_id, user_id, content, register_time, `like`
from review_comment
where review_id = #{review_id}

mapper.update:
update review_comment
set content = #{content}
where review_id = #{review_id} and comment_id = #{comment_id}

update review_comment
set `like` = `like` + 1
where review_id = #{review_id} and comment_id = #{comment_id}
